mapper.select:
SELECT d.DEVICE_ID, d.GROUP_ID, d.DEVICE_NAME, d.DEVICE_SYSTEM_NAME, d.DEVICE_IP, d.DEVICE_DESC, d.MODEL_ID, d.PORT_COUNT, m.MODEL_NAME, m.MODEL_OID, v.VENDOR_NAME, s.SNMP_VERSION, s.SNMP_PORT, s.SNMP_COMMUNITY, s.SNMP_USER, s.SNMP_AUTH_PROTOCOL, s.SNMP_AUTH_PASSWORD, s.SNMP_PRIV_PROTOCOL, s.SNMP_PRIV_PASSWORD, d.CREATE_USER_ID, d.CREATE_AT, d.MODIFY_USER_ID, d.MODIFY_AT, d.DELETE_USER_ID, d.DELETE_AT FROM r_device_t d LEFT JOIN r_device_snmp_t s ON d.DEVICE_ID = s.DEVICE_ID LEFT JOIN r_model_t m ON d.MODEL_ID = m.MODEL_ID LEFT JOIN r_vendor_t v ON m.VENDOR_ID = v.VENDOR_ID WHERE d.GROUP_ID IN
#{item}
AND d.DELETE_AT IS NULL

SELECT COUNT(*) FROM r_device_t d WHERE d.GROUP_ID IN
#{item}
AND d.DELETE_AT IS NULL

mapper.insert:
INSERT INTO r_device_snmp_t (
    DEVICE_ID, SNMP_VERSION, SNMP_PORT, SNMP_COMMUNITY, SNMP_USER,
    SNMP_AUTH_PROTOCOL, SNMP_AUTH_PASSWORD, SNMP_PRIV_PROTOCOL, SNMP_PRIV_PASSWORD
) VALUES (
    #{DEVICE_ID}, #{SNMP_VERSION}, #{SNMP_PORT}, #{SNMP_COMMUNITY}, #{SNMP_USER},
    #{SNMP_AUTH_PROTOCOL}, #{SNMP_AUTH_PASSWORD}, #{SNMP_PRIV_PROTOCOL}, #{SNMP_PRIV_PASSWORD}
)

mapper.update:
UPDATE r_device_t
GROUP_ID = #{GROUP_ID}, DEVICE_NAME = #{DEVICE_NAME}, DEVICE_SYSTEM_NAME = #{DEVICE_SYSTEM_NAME}, DEVICE_IP = #{DEVICE_IP}, DEVICE_DESC = #{DEVICE_DESC}, MODEL_ID = #{MODEL_ID}, PORT_COUNT = #{PORT_COUNT}, MODIFY_USER_ID = #{MODIFY_USER_ID}, MODIFY_AT = NOW()
WHERE DEVICE_ID = #{DEVICE_ID}

UPDATE r_device_snmp_t
SNMP_VERSION = #{SNMP_VERSION}, SNMP_PORT = #{SNMP_PORT}, SNMP_COMMUNITY = #{SNMP_COMMUNITY}, SNMP_USER = #{SNMP_USER}, SNMP_AUTH_PROTOCOL = #{SNMP_AUTH_PROTOCOL}, SNMP_AUTH_PASSWORD = #{SNMP_AUTH_PASSWORD}, SNMP_PRIV_PROTOCOL = #{SNMP_PRIV_PROTOCOL}, SNMP_PRIV_PASSWORD = #{SNMP_PRIV_PASSWORD},
WHERE DEVICE_ID = #{DEVICE_ID}

UPDATE r_device_t
SET DELETE_AT = NOW()
WHERE DEVICE_ID = #{deviceId}

