mapper.select:
SELECT h.ERROR_HISTORY_ID, h.ERROR_ID, h.DEVICE_ID, h.ERROR_CODE_ID, h.ERROR_MESSAGE, h.ERROR_LEVEL, h.OCCUR_AT, h.CLEAR_AT, h.USER_MESSAGE, h.UPDATE_AT, h.DEVICE_IP, d.DEVICE_NAME, g.GROUP_NAME FROM f_error_history_t h LEFT JOIN r_device_t d ON h.DEVICE_ID = d.DEVICE_ID LEFT JOIN r_group_t g ON d.GROUP_ID = g.GROUP_ID WHERE 1=1
AND h.ERROR_LEVEL = #{errorLevel}
AND h.DEVICE_ID = #{deviceId}
AND h.OCCUR_AT >= #{startDate}
AND h.OCCUR_AT <= CONCAT(#{endDate}, ' 23:59:59')
AND d.DEVICE_NAME LIKE CONCAT('%', #{deviceName}, '%')
AND h.DEVICE_IP LIKE CONCAT('%', #{deviceIp}, '%')
AND h.ERROR_MESSAGE LIKE CONCAT('%', #{errorMessage}, '%')
AND g.GROUP_NAME LIKE CONCAT('%', #{groupName}, '%')
ORDER BY h.CLEAR_AT DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM f_error_history_t h LEFT JOIN r_device_t d ON h.DEVICE_ID = d.DEVICE_ID LEFT JOIN r_group_t g ON d.GROUP_ID = g.GROUP_ID WHERE 1=1
AND h.ERROR_LEVEL = #{errorLevel}
AND h.DEVICE_ID = #{deviceId}
AND h.OCCUR_AT >= #{startDate}
AND h.OCCUR_AT <= CONCAT(#{endDate}, ' 23:59:59')
AND d.DEVICE_NAME LIKE CONCAT('%', #{deviceName}, '%')
AND h.DEVICE_IP LIKE CONCAT('%', #{deviceIp}, '%')
AND h.ERROR_MESSAGE LIKE CONCAT('%', #{errorMessage}, '%')
AND g.GROUP_NAME LIKE CONCAT('%', #{groupName}, '%')
